INSERT INTO ChampionSpell (key_board, champion_id, name, description, image) VALUES
('AnnieQ', '1', 'Desintegrar', 'Annie arremessa uma bola de fogo infundida em Mana, causando dano e recuperando o custo de Mana caso o alvo seja destruído.', 'https://ddragon.leagueoflegends.com/cdn/14.15.1/img/spell/AnnieQ.png'),
('AnnieW', '1', 'Incinerar', 'Annie conjura um ardente cone de fogo, causando dano em todos os inimigos na área.', 'https://ddragon.leagueoflegends.com/cdn/14.15.1/img/spell/AnnieW.png'),
('AnnieE', '1', 'Escudo Fundido', 'Annie concede um Escudo e um impulso de Velocidade de Movimento a si ou a um aliado, causando dano a inimigos que a atingirem com Ataques ou Habilidades.', 'https://ddragon.leagueoflegends.com/cdn/14.15.1/img/spell/AnnieE.png'),
('AnnieR', '1', 'Invocar: Tibbers', 'Annie dá vida ao urso Tibbers, causando dano a unidades na área. Tibbers pode atacar e também queimar inimigos que ficarem próximos a ele.', 'https://ddragon.leagueoflegends.com/cdn/14.15.1/img/spell/AnnieR.png'),
('OlafAxeThrowCast', '2', 'Ressaca', 'Olaf arremessa um machado no local-alvo, causando dano e reduzindo a Armadura e a Velocidade de Movimento dos inimigos que atravessar. Se Olaf apanhar o machado, o Tempo de Recarga da habilidade é redefinido.', 'https://ddragon.leagueoflegends.com/cdn/14.15.1/img/spell/OlafAxeThrowCast.png'),
('OlafFrenziedStrikes', '2', 'Duro na Queda', 'Olaf recebe aumento de Velocidade de Ataque e um Escudo.', 'https://ddragon.leagueoflegends.com/cdn/14.15.1/img/spell/OlafFrenziedStrikes.png'),
('OlafRecklessStrike', '2', 'Balanço Temerário', 'Olaf ataca com tal força que causa Dano Verdadeiro a seu alvo e a si mesmo, recuperando o custo de Vida caso destrua o alvo.', 'https://ddragon.leagueoflegends.com/cdn/14.15.1/img/spell/OlafRecklessStrike.png'),
('OlafRagnarok', '2', 'Ragnarok', 'Olaf recebe Armadura e Resistência Mágica adicionais passivamente. Ele pode ativar essa habilidade para se tornar imune a desarmes enquanto continuar atacando.', 'https://ddragon.leagueoflegends.com/cdn/14.15.1/img/spell/OlafRagnarok.png'),
('GalioQ', '3', 'Ventos de Guerra', 'Galio dispara duas rajadas de vento que convergem em um grande tornado e causam dano ao longo do tempo.', 'https://ddragon.leagueoflegends.com/cdn/14.15.1/img/spell/GalioQ.png'),
('GalioW', '3', 'Escudo de Durand', 'Galio carrega em postura defensiva, movendo-se lentamente. Ao soltar o carregamento, ele provocará e causará dano a inimigos próximos.', 'https://ddragon.leagueoflegends.com/cdn/14.15.1/img/spell/GalioW.png'),
('GalioE', '3', 'Soco Justiceiro', 'Galio dá um passo para trás e avança, arremessando ao ar o primeiro Campeão com que colidir.', 'https://ddragon.leagueoflegends.com/cdn/14.15.1/img/spell/GalioE.png'),
('GalioR', '3', 'Entrada Heroica', 'Galio marca a posição de um aliado como ponto de aterrissagem e concede um escudo mágico a todos os aliados na área. Após um intervalo, ele aterrissa no local, arremessando os inimigos próximos ao ar.', 'https://ddragon.leagueoflegends.com/cdn/14.15.1/img/spell/GalioR.png'),
('WildCards', '4', 'Curingas', 'Twisted Fate arremessa três cartas, causando dano a cada inimigo que elas atravessam.', 'https://ddragon.leagueoflegends.com/cdn/14.15.1/img/spell/WildCards.png'),
('PickACard', '4', 'Escolha uma Carta', 'Twisted Fate escolhe uma carta mágica de seu baralho e a usa em seu próximo ataque, causando efeitos adicionais.', 'https://ddragon.leagueoflegends.com/cdn/14.15.1/img/spell/PickACard.png'),
('CardmasterStack', '4', 'Baralho Marcado', 'A cada 4 ataques, Twisted Fate causa dano adicional. Além disso, sua Velocidade de Ataque aumenta.', 'https://ddragon.leagueoflegends.com/cdn/14.15.1/img/spell/CardmasterStack.png'),
('Destiny', '4', 'Destino', 'Twisted Fate prevê o futuro de seus adversários, revelando todos os Campeões inimigos e habilitando o uso de Portal, que o teleporta para qualquer local-alvo em 1,5s.', 'https://ddragon.leagueoflegends.com/cdn/14.15.1/img/spell/Destiny.png'),
('XinZhaoQ', '5', 'Golpe de Três Garras', 'O próximos 3 ataques normais de Xin Zhao causam mais dano. O último arremessa o oponente ao ar.', 'https://ddragon.leagueoflegends.com/cdn/14.15.1/img/spell/XinZhaoQ.png'),
('XinZhaoW', '5', 'Vento Vira Relâmpago', 'Xin Zhao golpeia à frente com a lança, causando Lentidão e marcando os inimigos atingidos como Desafiados.', 'https://ddragon.leagueoflegends.com/cdn/14.15.1/img/spell/XinZhaoW.png'),
('XinZhaoE', '5', 'Investida Audaciosa', 'Xin Zhao avança na direção de um inimigo, ganhando Velocidade de Ataque e causando dano e Lentidão a todos os inimigos na área temporariamente. Investida Audaciosa recebe alcance aumentado contra alvos Desafiados.', 'https://ddragon.leagueoflegends.com/cdn/14.15.1/img/spell/XinZhaoE.png'),
('XinZhaoR', '5', 'Guarda Crescente', 'Xin Zhao desafia um inimigo a quem tiver causado dano recentemente. Ele causa dano a inimigos próximos com base na Vida atual deles e Arremessa alvos não Desafiados para trás, tornando-se imune a qualquer dano causado por Campeões fora do círculo criado.', 'https://ddragon.leagueoflegends.com/cdn/14.15.1/img/spell/XinZhaoR.png'),
('UrgotQ', '6', 'Carga Corrosiva', 'Detona uma carga explosiva no local-alvo, causando Dano Físico e reduzindo a velocidade dos inimigos atingidos pela explosão.', 'https://ddragon.leagueoflegends.com/cdn/14.15.1/img/spell/UrgotQ.png'),
('UrgotW', '6', 'Expurgar', 'Urgot reduz sua velocidade enquanto descarrega sua arma contra inimigos próximos. Prioriza Campeões inimigos que tenha atingido recentemente com outras habilidades e aciona Chamas Ecoantes.', 'https://ddragon.leagueoflegends.com/cdn/14.15.1/img/spell/UrgotW.png'),
('UrgotE', '6', 'Desdém', 'Urgot avança em uma direção, protegendo-se com um escudo e pisoteando inimigos não Campeões. Se atingir um Campeão inimigo, ele para e o arremessa para fora de seu caminho.', 'https://ddragon.leagueoflegends.com/cdn/14.15.1/img/spell/UrgotE.png'),
('UrgotR', '6', 'Pior que a Morte', 'Urgot dispara uma broca química que perfura o primeiro Campeão inimigo atingido. Se esse Campeão ficar abaixo de um limite mínimo de Vida, Urgot o julgará fraco e o executará.', 'https://ddragon.leagueoflegends.com/cdn/14.15.1/img/spell/UrgotR.png'),
('LeblancQ', '7', 'Sigilo de Malícia', 'LeBlanc projeta um Sigilo, causando dano e marcando o alvo por 3,5s. Causar dano ao alvo marcado com qualquer Habilidade detona o Sigilo, causando dano adicional. Se qualquer parte dessa combinação abater o alvo, LeBlanc restitui o custo de Mana e parte do Tempo de Recarga restante desta Habilidade.', 'https://ddragon.leagueoflegends.com/cdn/14.15.1/img/spell/LeblancQ.png'),
('LeblancW', '7', 'Distorção', 'LeBlanc avança até um local, causando dano aos inimigos próximos de seu destino. Pelos próximos 4s, ativar Distorção novamente faz com que LeBlanc retorne até sua posição inicial.', 'https://ddragon.leagueoflegends.com/cdn/14.15.1/img/spell/LeblancW.png'),
('LeblancE', '7', 'Correntes Etéreas', 'LeBlanc lança uma corrente que prende a primeira unidade atingida. Se o alvo permanecer preso por 1,5s, ele é enraizado e sofre dano adicional.', 'https://ddragon.leagueoflegends.com/cdn/14.15.1/img/spell/LeblancE.png'),
('LeblancR', '7', 'Mímica', 'Leblanc conjura uma versão copiada de uma de suas habilidades.', 'https://ddragon.leagueoflegends.com/cdn/14.15.1/img/spell/LeblancR.png'),
('VladimirQ', '8', 'Transfusão', 'Vladimir rouba vida do inimigo-alvo. Quando sua reserva estiver cheia, Transfusão terá os benefícios de dano e cura drasticamente aumentados por um breve período.', 'https://ddragon.leagueoflegends.com/cdn/14.15.1/img/spell/VladimirQ.png'),
('VladimirSanguinePool', '8', 'Poça de Sangue', 'Vladimir mergulha numa poça de sangue, tornando-se inalvejável por 2s. Além disso, inimigos na poça sofrem redução de Velocidade de Movimento e Vladimir drena Vida deles.', 'https://ddragon.leagueoflegends.com/cdn/14.15.1/img/spell/VladimirSanguinePool.png'),
('VladimirE', '8', 'Maré de Sangue', 'Vladimir paga com a própria Vida para carregar uma reserva de sangue que, quando desferida, causa dano em área ao seu redor, mas pode ser bloqueada por unidades inimigas.', 'https://ddragon.leagueoflegends.com/cdn/14.15.1/img/spell/VladimirE.png'),
('VladimirHemoplague', '8', 'Hemopraga', 'Vladimir infecta uma área com uma praga virulenta. Inimigos afetados recebem dano crescente enquanto durar o efeito. Depois de alguns segundos, Hemopraga causa Dano Mágico aos inimigos infectados e cura Vladimir a cada Campeão inimigo atingido.', 'https://ddragon.leagueoflegends.com/cdn/14.15.1/img/spell/VladimirHemoplague.png'),
('FiddleSticksQ', '9', 'Aterrorizar', 'Ao causar dano a inimigos com habilidades enquanto não estiver sendo visto ou alvejar um inimigo com Aterrorizar, Fiddlesticks inflige medo em uma unidade-alvo inimiga, fazendo com que fuja aterrorizada por um curto período.', 'https://ddragon.leagueoflegends.com/cdn/14.15.1/img/spell/FiddleSticksQ.png'),
('FiddleSticksW', '9', 'Colheita Farta', 'Fiddlesticks drena a Vida dos inimigos próximos, causando dano de execução adicional no fim da duração.', 'https://ddragon.leagueoflegends.com/cdn/14.15.1/img/spell/FiddleSticksW.png'),
('FiddleSticksE', '9', 'Ceifar', 'Fiddlesticks ceifa uma área com sua foice, reduzindo a velocidade de movimento de todos os inimigos atingidos e silenciando os atingidos no centro do corte.', 'https://ddragon.leagueoflegends.com/cdn/14.15.1/img/spell/FiddleSticksE.png'),
('FiddleSticksR', '9', 'Tempestade de Corvos', 'Uma revoada de corvos assassinos voa em volta de Fiddlesticks, causando dano por segundo a todas as unidades inimigas próximas.', 'https://ddragon.leagueoflegends.com/cdn/14.15.1/img/spell/FiddleSticksR.png'),
('KayleQ', '10', 'Explosão Radiante', 'Kayle conjura um portal, invocando uma espada celestial que atravessa os inimigos, causando dano e reduzindo a velocidade e as resistências de tudo que atingir.', 'https://ddragon.leagueoflegends.com/cdn/14.15.1/img/spell/KayleQ.png'),
('KayleW', '10', 'Bênção Celestial', 'Abençoada pelas divindades, Kayle cura e concede Velocidade de Movimento a si mesma e ao aliado mais próximo.', 'https://ddragon.leagueoflegends.com/cdn/14.15.1/img/spell/KayleW.png'),
('KayleE', '10', 'Lâmina de Fogo Estelar', 'Passivo: a espada celestial de Kayle, Virtude, causa Dano Mágico adicional aos inimigos que ela ataca.<br><br>Ativo: o próximo ataque de Kayle atinge seu alvo com fogo celestial, causando dano adicional proporcional à Vida perdida do alvo.', 'https://ddragon.leagueoflegends.com/cdn/14.15.1/img/spell/KayleE.png'),
('KayleR', '10', 'Sentença Divina', 'Kayle torna um aliado invulnerável e evoca os antigos Aspectos da Justiça para purificarem a área ao redor do seu alvo com uma chuva de espadas sagradas.', 'https://ddragon.leagueoflegends.com/cdn/14.15.1/img/spell/KayleR.png'),
('AlphaStrike', '11', 'Ataque Alpha', 'Master Yi se teleporta pelo campo de batalha tão rápido que não pode ser visto, causando Dano Físico a várias unidades pelo caminho enquanto fica inalvejável. O Ataque Alpha pode causar Acerto Crítico e Dano Físico adicional contra monstros. Ataques básicos reduzem o Tempo de Recarga de Ataque Alpha.', 'https://ddragon.leagueoflegends.com/cdn/14.15.1/img/spell/AlphaStrike.png'),
('Meditate', '11', 'Meditar', 'Master Yi rejuvenesce seu corpo com a força da mente, restaurando Vida e recebendo dano reduzido por um curto período. Além disso, ele recebe acúmulos de Ataque Duplo e pausa a duração restante de Estilo Wuju e de Highlander para cada segundo de canalização.', 'https://ddragon.leagueoflegends.com/cdn/14.15.1/img/spell/Meditate.png'),
('WujuStyle', '11', 'Estilo Wuju', 'Concede Dano Verdadeiro adicional em ataques básicos.', 'https://ddragon.leagueoflegends.com/cdn/14.15.1/img/spell/WujuStyle.png'),
('Highlander', '11', 'Highlander', 'Master Yi se movimenta com extrema agilidade, aumentando temporariamente suas Velocidades de Ataque e de Movimento, tornando-se imune a todos os efeitos de Lentidão. Abates ou assistências de Campeões estendem a duração de Highlander enquanto a habilidade estiver ativa. Reduz passivamente o Tempo de Recarga das outras habilidades a cada abate ou assistência.', 'https://ddragon.leagueoflegends.com/cdn/14.15.1/img/spell/Highlander.png'),
('Pulverize', '12', 'Pulverizar', 'Alistar golpeia o chão, causando dano a inimigos próximos e arremessando-os ao ar.', 'https://ddragon.leagueoflegends.com/cdn/14.15.1/img/spell/Pulverize.png'),
('Headbutt', '12', 'Cabeçada', 'Alistar atinge o inimigo com sua cabeça, causando dano e jogando o alvo para trás.', 'https://ddragon.leagueoflegends.com/cdn/14.15.1/img/spell/Headbutt.png'),
('AlistarE', '12', 'Atropelar', 'Alistar atropela unidades inimigas próximas, ignorando colisão com unidades e recebendo acúmulos caso cause dano a um Campeão inimigo. O próximo ataque básico com o máximo de acúmulos contra um Campeão inimigo causa Dano Mágico adicional e o atordoa.', 'https://ddragon.leagueoflegends.com/cdn/14.15.1/img/spell/AlistarE.png'),
('FerociousHowl', '12', 'Vontade Indestrutível', 'Alistar solta um urro selvagem, removendo todos os efeitos de Controle de Grupo de si mesmo, além de reduzir o Dano Físico e Mágico enquanto durar o efeito.', 'https://ddragon.leagueoflegends.com/cdn/14.15.1/img/spell/FerociousHowl.png'),
('RyzeQWrapper', '13', 'Sobrecarregar', 'Passivamente, as outras habilidades básicas de Ryze redefinem o Tempo de Recarga de Sobrecarregar e carregam uma runa. Quando Ryze conjura Sobrecarregar com 2 runas carregadas, ele recebe um breve surto de Velocidade de Movimento.<br><br>Ao conjurar, Ryze arremessa uma carga de energia pura em linha reta, causando dano ao primeiro inimigo atingido. Se o alvo estiver afetado por Fluxo, Sobrecarregar causa dano adicional e rebate para inimigos próximos atingidos por Fluxo.', 'https://ddragon.leagueoflegends.com/cdn/14.15.1/img/spell/RyzeQWrapper.png'),
('RyzeW', '13', 'Prisão de Runa', 'Ryze prende um alvo em uma prisão de runas, causando dano e reduzindo sua Velocidade de Movimento. Se o alvo estiver afetado por Fluxo, ele é enraizado.', 'https://ddragon.leagueoflegends.com/cdn/14.15.1/img/spell/RyzeW.png'),
('RyzeE', '13', 'Fluxo de Feitiço', 'Ryze lança um orbe de puro poder mágico que causa dano a um inimigo e um efeito negativo em todos os inimigos próximos. As habilidades de Ryze têm efeitos adicionais contra a unidade afetada.', 'https://ddragon.leagueoflegends.com/cdn/14.15.1/img/spell/RyzeE.png'),
('RyzeR', '13', 'Portal de Reinos', 'Passivamente, Sobrecarregar causa ainda mais dano contra alvos afetados por Fluxo.<br><br>Ao conjurar, Ryze cria um portal para um local próximo. Após alguns segundos, aliados próximos ao portal são teleportados ao local alvo.', 'https://ddragon.leagueoflegends.com/cdn/14.15.1/img/spell/RyzeR.png'),
('SionQ', '14', 'Golpe Demolidor', 'Sion carrega um poderoso golpe em uma área à sua frente que causará dano a inimigos quando lançado. Caso carregue por tempo suficiente, também arremessará ao ar e atordoará os inimigos atingidos.', 'https://ddragon.leagueoflegends.com/cdn/14.15.1/img/spell/SionQ.png'),
('SionW', '14', 'Fornalha da Alma', 'Sion se protege e pode reativar após 3 segundos para causar Dano Mágico a inimigos próximos. Quando Sion abate inimigos, ele recebe Vida máxima passivamente.', 'https://ddragon.leagueoflegends.com/cdn/14.15.1/img/spell/SionW.png'),
('SionE', '14', 'Urro do Assassino', 'Sion dispara uma onda de choque de curto alcance que causa dano e reduz a velocidade e Armadura do primeiro inimigo atingido. Se ela atingir uma tropa ou monstro, o mesmo será arremessado para trás, causando dano e reduzindo a velocidade e Armadura de todos os inimigos que atravessar.', 'https://ddragon.leagueoflegends.com/cdn/14.15.1/img/spell/SionE.png'),
('SionR', '14', 'Investida Incontrolável', 'Sion avança em linha reta, acelerando ao longo do tempo. Ele pode guiar levemente sua investida com o cursor do mouse na posição. Ao colidir com um inimigo, ele causa dano e o arremessa ao ar com base na distância percorrida.', 'https://ddragon.leagueoflegends.com/cdn/14.15.1/img/spell/SionR.png'),
('SivirQ', '15', 'Lâmina Bumerangue', 'Sivir arremessa sua lâmina como um bumerangue, causando dano tanto na ida quanto na volta.', 'https://ddragon.leagueoflegends.com/cdn/14.15.1/img/spell/SivirQ.png'),
('SivirW', '15', 'Ricochete', 'Os próximos ataques básicos de Sivir recebem Velocidade de Ataque adicional e ricocheteiam em alvos próximos, causando dano reduzido enquanto rebatem.', 'https://ddragon.leagueoflegends.com/cdn/14.15.1/img/spell/SivirW.png'),
('SivirE', '15', 'Escudo de Feitiço', 'Cria uma barreira mágica que bloqueia a habilidade de um único inimigo lançada em Sivir. Ela recebe Vida e uma explosão de Velocidade de Movimento se uma habilidade for bloqueada.', 'https://ddragon.leagueoflegends.com/cdn/14.15.1/img/spell/SivirE.png'),
('SivirR', '15', 'Na Caçada', 'Sivir conduz os aliados até a batalha, concedendo uma explosão de Velocidade de Movimento a eles por um curto período. Além disso, os ataques de Sivir reduzem o Tempo de Recarga das habilidades.', 'https://ddragon.leagueoflegends.com/cdn/14.15.1/img/spell/SivirR.png'),
('SorakaQ', '16', 'Chamado Estelar', 'Uma estrela cai do céu no local-alvo, causando Dano Mágico e reduzindo a velocidade de inimigos. Se um Campeão inimigo for atingido por Chamado Estelar, Soraka regenera Vida.', 'https://ddragon.leagueoflegends.com/cdn/14.15.1/img/spell/SorakaQ.png'),
('SorakaW', '16', 'Infusão Astral', 'Soraka sacrifica parte de sua própria Vida para curar outro Campeão aliado.', 'https://ddragon.leagueoflegends.com/cdn/14.15.1/img/spell/SorakaW.png'),
('SorakaE', '16', 'Equinócio', 'Cria uma área em um local, silenciando todos os inimigos dentro dela. Quando a área expira, os que ainda estiverem dentro são enraizados.', 'https://ddragon.leagueoflegends.com/cdn/14.15.1/img/spell/SorakaE.png'),
('SorakaR', '16', 'Desejo', 'Soraka enche seus aliados de esperança, instantaneamente restaurando Vida para si mesma e todos os Campeões aliados.', 'https://ddragon.leagueoflegends.com/cdn/14.15.1/img/spell/SorakaR.png'),
('BlindingDart', '17', 'Dardo Ofuscante', 'Obscurece a visão de um inimigo com um poderoso veneno, causando dano à unidade-alvo e cegando-a pela duração do efeito.', 'https://ddragon.leagueoflegends.com/cdn/14.15.1/img/spell/BlindingDart.png'),
('MoveQuick', '17', 'Mover Depressa', 'Teemo corre por aí, aumentando passivamente sua Velocidade de Movimento até que seja atingido por um Campeão ou torre inimiga. Ele pode sair em disparada para receber Velocidade de Movimento adicional que não é interrompida por ataques por um certo período.', 'https://ddragon.leagueoflegends.com/cdn/14.15.1/img/spell/MoveQuick.png'),
('ToxicShot', '17', 'Tiro Tóxico', 'Cada um dos ataques de Teemo envenenará o alvo, causando dano no impacto e a cada segundo seguinte por 4 segundos.', 'https://ddragon.leagueoflegends.com/cdn/14.15.1/img/spell/ToxicShot.png'),
('TeemoRCast', '17', 'Armadilha Venenosa', 'Teemo arremessa uma armadilha venenosa explosiva usando um dos cogumelos que guardou na mochila. Se um inimigo pisar na armadilha, ela soltará uma nuvem venenosa que causa dano e reduz a velocidade de inimigos ao longo do tempo. Se Teemo arremessar um cogumelo em cima de outro, ele saltará, recebendo alcance adicional.', 'https://ddragon.leagueoflegends.com/cdn/14.15.1/img/spell/TeemoRCast.png'),
('TristanaQ', '18', 'Tiro Rápido', 'Tristana dispara rapidamente com seu canhão, aumentando sua Velocidade de Ataque por um curto período de tempo.', 'https://ddragon.leagueoflegends.com/cdn/14.15.1/img/spell/TristanaQ.png'),
('TristanaW', '18', 'Salto-foguete', 'Tristana dispara no chão para lançar-se ao local-alvo, causando dano e reduzindo a Velocidade de Movimento das unidades ao seu redor por um breve momento ao aterrissar.', 'https://ddragon.leagueoflegends.com/cdn/14.15.1/img/spell/TristanaW.png'),
('TristanaE', '18', 'Carga Explosiva', 'Quando Tristana abate uma unidade, seus projéteis explodem com estilhaços, causando dano a inimigos próximos. Isso pode ser ativado para posicionar uma bomba em um alvo inimigo, que explodirá após uma curta duração, causando dano a unidades ao seu redor.', 'https://ddragon.leagueoflegends.com/cdn/14.15.1/img/spell/TristanaE.png'),
('TristanaR', '18', 'Tiro Destruidor', 'Tristana carrega uma gigantesca bala de canhão em sua arma e a dispara em uma unidade inimiga. Isso causa Dano Mágico e empurra o alvo para trás. Se o alvo estiver carregando uma bomba da Carga Explosiva, o raio de explosão da bomba é dobrado.', 'https://ddragon.leagueoflegends.com/cdn/14.15.1/img/spell/TristanaR.png'),
('WarwickQ', '19', 'Presas da Fera', 'Warwick lança-se à frente e morde seu alvo, causando dano com base em sua Vida máxima e curando a si mesmo com o dano causado.', 'https://ddragon.leagueoflegends.com/cdn/14.15.1/img/spell/WarwickQ.png'),
('WarwickW', '19', 'Caçada Sangrenta', 'Warwick percebe inimigos com menos de 50% Vida, recebendo Velocidade de Movimento ao movimentar-se na direção deles e Velocidade de Ataque ao afastar-se deles. Se ficarem com menos de 20% de Vida, ele entra em frenesi e os efeitos são triplicados.', 'https://ddragon.leagueoflegends.com/cdn/14.15.1/img/spell/WarwickW.png'),
('WarwickE', '19', 'Uivo Primitivo', 'Warwick recebe redução de dano por 2,5 segundos. Ao fim do efeito, ou caso seja reativado, ele uiva, fazendo com que inimigos próximos fujam por 1 segundo.', 'https://ddragon.leagueoflegends.com/cdn/14.15.1/img/spell/WarwickE.png'),
('WarwickR', '19', 'Coerção Infinita', 'Warwick salta em uma direção (escalando com a Velocidade de Movimento adicional dele), suprimindo o primeiro Campeão atingido por 1,5s.', 'https://ddragon.leagueoflegends.com/cdn/14.15.1/img/spell/WarwickR.png'),
('NunuQ', '20', 'Consumir', 'Willump dá uma mordida em uma tropa, monstro ou Campeão inimigo, causando dano e se curando.', 'https://ddragon.leagueoflegends.com/cdn/14.15.1/img/spell/NunuQ.png'),
('NunuW', '20', 'A Maior Bola de Neve de Todas!', 'Willump cria uma bola de neve que aumenta em velocidade e tamanho conforme ele a rola. Ela causa dano a inimigos e os arremessa ao ar.', 'https://ddragon.leagueoflegends.com/cdn/14.15.1/img/spell/NunuW.png'),
('NunuE', '20', 'Rajada de Bolas de Neve', 'Nunu arremessa múltiplas bolas de neve que causam dano a inimigos. Ao terminar, Willump enraíza quaisquer Campeões ou monstros grandes que foram atingidos por uma delas.', 'https://ddragon.leagueoflegends.com/cdn/14.15.1/img/spell/NunuE.png'),
('NunuR', '20', 'Zero Absoluto', 'Nunu e Willump criam uma poderosa nevasca em uma área, causando lentidão aos inimigos e um dano massivo quando se encerrar.', 'https://ddragon.leagueoflegends.com/cdn/14.15.1/img/spell/NunuR.png'),
('MissFortuneRicochetShot', '21', 'Dois por Um', 'Miss Fortune dispara contra um inimigo, causando dano a ele e a um alvo atrás dele. Ambos os acertos também podem aplicar Batida do Amor.', 'https://ddragon.leagueoflegends.com/cdn/14.15.1/img/spell/MissFortuneRicochetShot.png'),
('MissFortuneViciousStrikes', '21', 'Desfilando', 'Miss Fortune ganha Velocidade de Movimento passivamente quando não está sendo atacada. Esta habilidade pode ser ativada para conceder Velocidade de Ataque adicional por um curto período. Enquanto está em Tempo de Recarga, Batida do Amor reduz o Tempo de Recarga restante de Desfilando.', 'https://ddragon.leagueoflegends.com/cdn/14.15.1/img/spell/MissFortuneViciousStrikes.png'),
('MissFortuneScattershot', '21', 'Chuva de Disparos', 'Miss Fortune revela uma área com uma saraivada de balas, causando ondas de dano e reduzindo a velocidade dos oponentes.', 'https://ddragon.leagueoflegends.com/cdn/14.15.1/img/spell/MissFortuneScattershot.png'),
('MissFortuneBulletTime', '21', 'Metendo Bala', 'Miss Fortune canaliza uma saraivada de balas em formato de cone à sua frente, causando muito dano aos inimigos. Cada remessa de Metendo Bala pode causar Acerto Crítico.', 'https://ddragon.leagueoflegends.com/cdn/14.15.1/img/spell/MissFortuneBulletTime.png'),
